    endDate: new Date(new Date().setDate(new Date().getDate() + 2)),
    startTime: '17:00',
    endTime: '17:30',
    isRepeating: false,
    repeatingType: null,
    location: 'Platform Manajemen Proyek',
    category: 'Proyek',
    color: 'blue',
    createdAt: new Date(),
    updatedAt: new Date(),
  },
  {
    id: '3',
    title: 'Review Mingguan',
    description: 'Review minggu lalu dan perencanaan untuk minggu depan.',
    startDate: new Date(
      new Date().setDate(new Date().getDate() - new Date().getDay() + 5),
    ),
    endDate: new Date(
      new Date().setDate(new Date().getDate() - new Date().getDay() + 5),
    ),
    startTime: '15:00',
    endTime: '16:00',
    isRepeating: true,
    repeatingType: 'weekly',
    location: 'Ruang Konferensi B',
    category: 'Pekerjaan',
    color: 'yellow',
    createdAt: new Date(),
    updatedAt: new Date(),
  },
  {
    id: '4',
    title: 'Janji Temu Dokter Gigi',
    description: 'Pemeriksaan tahunan.',
    startDate: new Date(new Date().setDate(new Date().getDate() + 10)),
    endDate: new Date(new Date().setDate(new Date().getDate() + 10)),
    startTime: '11:00',
    endTime: '12:00',
    isRepeating: false,
    repeatingType: null,
    location: 'Klinik Gigi Kota',
    category: 'Pribadi',
    color: 'purple',
    createdAt: new Date(),
    updatedAt: new Date(),
  },
];

interface DemoPageProps {
  searchParams: Promise<SearchParams>;
}

export default async function DemoPage(props: DemoPageProps) {
  const searchParams = await props.searchParams;
  const search = searchParamsCache.parse(searchParams);

  const eventsResponse = {
    events: dummyEvents,
  };

  return (
    <div className="flex min-h-screen flex-col">
      <main className="flex-1 py-6">
        <div className="container">
          <div className="bg-card overflow-hidden rounded-xl border shadow-sm">
            <Suspense
              fallback={
                <div className="flex h-[700px] items-center justify-center">
                  <div className="flex flex-col items-center gap-2">
                    <div className="border-primary h-8 w-8 animate-spin rounded-full border-4 border-t-transparent"></div>
                    <p className="text-muted-foreground text-sm">
                      Loading calendar...
                    </p>
                  </div>
                </div>
              }
            >
              <EventCalendar
                events={eventsResponse.events}
                initialDate={search.date}
              />
            </Suspense>
          </div>
        </div>
      </main>
    </div>
  );
}

'use client';

import { EventsList } from './event-list';
import { EventCalendarDay } from './event-calendar-day';
import { EventCalendarWeek } from './event-calendar-week';
import EventDialog from './event-dialog';
import { useEventCalendarStore } from '@/hooks/use-event';
import { EventCalendarMonth } from './event-calendar-month';
import { MonthDayEventsDialog } from './day-events-dialog';
import EventCreateDialog from './event-create-dialog';
import { useShallow } from 'zustand/shallow';
import { useMemo } from 'react';
import { EventCalendarYear } from './event-calendar-year';
import { EventCalendarDays } from './event-calendar-days';
import CalendarToolbar from './event-calendar-toolbar';
import { Events } from '@/types/event';

interface EventCalendarProps {
  events: Events[];
  initialDate: Date;
}

export function EventCalendar({ initialDate, events }: EventCalendarProps) {
  const { viewMode, currentView, daysCount } = useEventCalendarStore(
    useShallow((state) => ({
      viewMode: state.viewMode,
      currentView: state.currentView,
      daysCount: state.daysCount,
    })),
  );

  const renderCalendarView = useMemo(() => {
    if (viewMode === 'list') {
      return <EventsList events={events} currentDate={initialDate} />;
    }
    switch (currentView) {
      case 'day':
        return <EventCalendarDay events={events} currentDate={initialDate} />;
      case 'days':
        return (
          <EventCalendarDays
            events={events}
            daysCount={daysCount}
            currentDate={initialDate}
          />
        );
      case 'week':
        return <EventCalendarWeek events={events} currentDate={initialDate} />;
      case 'month':
        return <EventCalendarMonth events={events} baseDate={initialDate} />;
      case 'year':
        return <EventCalendarYear events={events} currentDate={initialDate} />;
      default:
        return <EventCalendarDay events={events} currentDate={initialDate} />;
    }
  }, [currentView, daysCount, events, initialDate, viewMode]);

  return (
    <>
      <EventDialog />
      <MonthDayEventsDialog />
      <EventCreateDialog />
      <div className="bg-background overflow-hidden rounded-xl border shadow-sm">
        <CalendarToolbar />
        <div className="overflow-hidden p-0">{renderCalendarView}</div>
      </div>
    </>
  );
}

'use client';

import { useState, useMemo, useRef, useCallback } from 'react';
import { ScrollArea } from '../ui/scroll-area';
import { isSameDay } from 'date-fns';
import { generateTimeSlots } from '@/lib/date';
import { cn } from '@/lib/utils';
import { Events, HoverPositionType } from '@/types/event';
import { EventDialogTrigger } from './event-dialog-trigger';
import { CurrentTimeIndicator } from './ui/current-time-indicator';
import { HoverTimeIndicator } from './ui/hover-time-indicator';
import { useDayEventPositions } from '@/lib/event';
import { TimeColumn } from './ui/time-column';
import { useEventCalendarStore } from '@/hooks/use-event';
import { useShallow } from 'zustand/shallow';

const HOUR_HEIGHT = 64; // Height in pixels for 1 hour
const START_HOUR = 0; // 00:00
const END_HOUR = 23; // 23:00
const COLUMN_WIDTH_TOTAL = 99.5; // Total width percentage for columns

interface CalendarDayProps {
  events: Events[];
  currentDate: Date;
}
export function EventCalendarDay({ events, currentDate }: CalendarDayProps) {
  const { timeFormat, viewSettings, openQuickAddDialog, openEventDialog } =
    useEventCalendarStore(
      useShallow((state) => ({
        timeFormat: state.timeFormat,
        viewSettings: state.viewSettings,
        openQuickAddDialog: state.openQuickAddDialog,
        openEventDialog: state.openEventDialog,
      })),
    );
  const [hoverPosition, setHoverPosition] = useState<
    HoverPositionType | undefined
  >(undefined);
  const timeColumnRef = useRef<HTMLDivElement>(null);

  const now = new Date();
  const currentHour = now.getHours();
  const currentMinute = now.getMinutes();

  const filteredEvents = useMemo(() => {
    return events.filter((event) => {
      const eventStartDate = new Date(event.startDate);
      const eventEndDate = new Date(event.endDate);

      return (
        isSameDay(eventStartDate, currentDate) ||
        isSameDay(eventEndDate, currentDate) ||
        (currentDate > eventStartDate && currentDate < eventEndDate)
      );
    });
  }, [events, currentDate]);

  const timeSlots = useMemo(() => generateTimeSlots(START_HOUR, END_HOUR), []);
  const eventsPositions = useDayEventPositions(events, HOUR_HEIGHT);

  const handleTimeHover = useCallback((hour: number) => {
    setHoverPosition((prev) => ({ ...prev, hour, minute: 0, dayIndex: -1 }));
  }, []);

  const handlePreciseHover = useCallback(
    (event: React.MouseEvent<HTMLButtonElement>, hour: number) => {
      if (!timeColumnRef.current) return;

      const slotRect = event.currentTarget.getBoundingClientRect();
      const cursorY = event.clientY - slotRect.top;
      const minutes = Math.floor((cursorY / slotRect.height) * 60);

      setHoverPosition({
        hour,
        minute: Math.max(0, Math.min(59, minutes)),
        dayIndex: -1,
      });
    },
    [],
  );

  const handleTimeLeave = useCallback(() => {
    setHoverPosition(undefined);
  }, []);

  const handleTimeSlotClick = useCallback(() => {
    if (!viewSettings.day.enableTimeSlotClick || !hoverPosition) return;

    openQuickAddDialog({
      date: currentDate,
      position: hoverPosition,
    });
  }, [
    currentDate,
    hoverPosition,
    openQuickAddDialog,
    viewSettings.day.enableTimeSlotClick,
  ]);

  return (
    <div className="flex h-[760px] flex-col py-3">
      <ScrollArea className="h-full w-full rounded-md px-4">
        <div className="relative mt-2 mb-2">
          <div className="absolute left-0 z-10 w-13">
            <TimeColumn
              ref={timeColumnRef}
              timeSlots={timeSlots}
              timeFormat={timeFormat}
              onTimeHover={handleTimeHover}
              onPreciseHover={handlePreciseHover}
              onLeave={handleTimeLeave}
              onTimeSlotClick={handleTimeSlotClick}
              variant="day"
            />
          </div>
          <div className="relative ml-14">
            {viewSettings.day.showCurrentTimeIndicator && (
              <CurrentTimeIndicator
                currentHour={currentHour}
                currentMinute={currentMinute}
                timeFormat={timeFormat}
                hourHeight={HOUR_HEIGHT}
                className="left-0"
              />
            )}
            {hoverPosition && viewSettings.day.showHoverTimeIndicator && (
              <HoverTimeIndicator
                hour={hoverPosition.hour}
                minute={hoverPosition.minute}
                timeFormat={timeFormat}
                hourHeight={HOUR_HEIGHT}
                className="left-0"
              />
            )}
            {timeSlots.map((time, index) => (
              <div
                key={index}
                data-testid={`time-grid-${index}`}
                className={cn('border-border h-16 border-t')}
              />
            ))}
            {filteredEvents.map((event) => {
              const position = eventsPositions[event.id];
              if (!position) return null;

              const columnWidth = COLUMN_WIDTH_TOTAL / position.totalColumns;
              const leftPercent = position.column * columnWidth;
              const rightPercent =
                COLUMN_WIDTH_TOTAL - (leftPercent + columnWidth);

              return (
                <EventDialogTrigger
                  event={event}
                  key={event.id}
                  position={position}
                  leftOffset={leftPercent}
                  rightOffset={rightPercent}
                  onClick={openEventDialog}
                />
              );
            })}
          </div>
        </div>
      </ScrollArea>
    </div>
  );
}

'use client';

import { useCallback, useMemo, useRef, useState } from 'react';
import { formatDate, generateTimeSlots, isSameDay } from '@/lib/date';
import { ScrollArea } from '../ui/scroll-area';
import { WeekDayHeaders } from './ui/week-days-header';
import { TimeGrid } from './ui/time-grid';
import { EventDialogTrigger } from './event-dialog-trigger';
import {
  getLocaleFromCode,
  useEventPositions,
  useFilteredEvents,
  useMultiDayEventRows,
  useWeekDays,
} from '@/lib/event';
import { useEventCalendarStore } from '@/hooks/use-event';
import { useShallow } from 'zustand/shallow';
import { MultiDayEventSection } from './ui/multi-day-event';
import { TimeColumn } from './ui/time-column';
import { Events, HoverPositionType } from '@/types/event';
import { CurrentTimeIndicator } from './ui/current-time-indicator';
import { HoverTimeIndicator } from './ui/hover-time-indicator';
import { cn } from '@/lib/utils';
import { Tooltip, TooltipContent, TooltipTrigger } from '../ui/tooltip';
import { Button } from '../ui/button';
import { ChevronDown, ChevronUp } from 'lucide-react';

const HOUR_HEIGHT = 64;
const START_HOUR = 0;
const END_HOUR = 23;
const MULTI_DAY_ROW_HEIGHT = 65;

interface CalendarDayViewProps {
  events: Events[];
  currentDate: Date;
  daysCount: number;
}

export function EventCalendarDays({
  events,
  currentDate,
  daysCount = 16,
}: CalendarDayViewProps) {
  const {
    timeFormat,
    locale,
    firstDayOfWeek,
    viewSettings,
    openEventDialog,
    openQuickAddDialog,
  } = useEventCalendarStore(
    useShallow((state) => ({
      timeFormat: state.timeFormat,
      locale: state.locale,
      viewSettings: state.viewSettings,
      firstDayOfWeek: state.firstDayOfWeek,
      openEventDialog: state.openEventDialog,
      openQuickAddDialog: state.openQuickAddDialog,
    })),
  );
  const [hoverPosition, setHoverPosition] = useState<
    HoverPositionType | undefined
  >(undefined);
  const [isMultiDayExpanded, setIsMultiDayExpanded] = useState(false);
  const containerRef = useRef<HTMLDivElement>(null);
  const timeColumnRef = useRef<HTMLDivElement>(null);

  const now = new Date();
  const currentHour = now.getHours();
  const currentMinute = now.getMinutes();

  const localeObj = getLocaleFromCode(locale);

  const dayWidthPercent = 100 / daysCount;
  const { weekDays, todayIndex } = useWeekDays(
    currentDate,
    daysCount,
    localeObj,
  );

  const { singleDayEvents, multiDayEvents } = useFilteredEvents(
    events,
    weekDays,
  );
  const eventsPositions = useEventPositions(
    singleDayEvents,
    weekDays,
    HOUR_HEIGHT,
  );
  const multiDayEventRows = useMultiDayEventRows(multiDayEvents, weekDays);
  const timeSlots = useMemo(() => generateTimeSlots(START_HOUR, END_HOUR), []);

  const totalMultiDayRows =
    multiDayEventRows.length > 0
      ? Math.max(...multiDayEventRows.map((r) => r.row)) + 1
      : 1;

  const handleTimeHover = useCallback((hour: number) => {
    setHoverPosition((prev) => ({ ...prev, hour, minute: 0, dayIndex: -1 }));
  }, []);

  const handlePreciseHover = useCallback(
    (event: React.MouseEvent<HTMLButtonElement>, hour: number) => {
      if (!timeColumnRef.current) return;

      const slotRect = event.currentTarget.getBoundingClientRect();
      const cursorY = event.clientY - slotRect.top;
      const minutes = Math.floor((cursorY / slotRect.height) * 60);

      setHoverPosition({
        hour,
        minute: Math.max(0, Math.min(59, minutes)),
        dayIndex: -1,
      });
    },
    [],
  );

  const handleTimeLeave = useCallback(() => {
    setHoverPosition(undefined);
  }, []);

  const handleTimeSlotClick = useCallback(() => {
    if (!viewSettings.days.enableTimeSlotClick || !hoverPosition) return;

    openQuickAddDialog({
      date: currentDate,
      position: hoverPosition,
    });
  }, [
    currentDate,
    hoverPosition,
    openQuickAddDialog,
    viewSettings.days.enableTimeSlotClick,
  ]);

  const showEventDetail = useCallback(
    (_event: Events) => {
      openEventDialog(_event);
    },
    [openEventDialog],
  );

  const handleTimeBlockClick = useCallback(
    (data: { date: Date; startTime: string; endTime: string }) => {
      if (!viewSettings.days.enableTimeBlockClick) return;
      openQuickAddDialog({
        date: data.date,
        startTime: data.startTime,
        endTime: data.endTime,
        position: hoverPosition,
      });
    },
    [hoverPosition, openQuickAddDialog, viewSettings.days.enableTimeBlockClick],
  );

  const toggleMultiDayExpand = useCallback(() => {
    setIsMultiDayExpanded((prev) => !prev);
  }, []);

  return (
    <div className="flex h-full flex-col overflow-hidden border">
      <div className="bg-background border-border sticky top-0 z-30">
        <div className="flex py-2">
          <div className="w-[52px]" />
          <WeekDayHeaders
            daysInWeek={weekDays}
            formatDate={formatDate}
            locale={localeObj}
            firstDayOfWeek={firstDayOfWeek}
            highlightToday
            showDayNumber
          />
        </div>
      </div>
      {multiDayEventRows.length > 0 &&
        viewSettings.days.expandMultiDayEvents && (
          <div className="bg-background border-border sticky top-[48px] z-20 flex border-b">
            <div className="flex h-[64px] w-[51px] items-center justify-center">
              {multiDayEventRows.length > 1 && (
                <Tooltip>
                  <TooltipTrigger asChild>
                    <Button
                      variant="ghost"
                      size="icon"
                      className="text-muted-foreground hover:text-primary h-10 w-10"
                      onClick={toggleMultiDayExpand}
                    >
                      <span className="sr-only">
                        {isMultiDayExpanded ? 'Collapse' : 'Expand'} multi-day
                      </span>
                      {isMultiDayExpanded ? <ChevronUp /> : <ChevronDown />}
                    </Button>
                  </TooltipTrigger>
                  <TooltipContent>
                    {isMultiDayExpanded ? 'Collapse' : 'Expand'} multi-day
                  </TooltipContent>
                </Tooltip>
              )}
            </div>
            <div
              className="relative flex-1"
              style={{
                height: isMultiDayExpanded
                  ? `${totalMultiDayRows * MULTI_DAY_ROW_HEIGHT}px`
                  : `${MULTI_DAY_ROW_HEIGHT}px`,
                transition: 'height 0.3s ease',
              }}
            >
              <div className="absolute inset-0">
                <div className="relative">
                  {Array.from({
                    length: isMultiDayExpanded ? totalMultiDayRows : 1,
                  }).map((_, rowIndex) => (
                    <div
                      key={`multi-day-row-${rowIndex}`}
                      className="border-border flex h-16 border-t"
                      style={{ height: `${MULTI_DAY_ROW_HEIGHT}px` }}
                    >
                      {weekDays.map((day, dayIndex) => (
                        <div
                          key={`multi-day-cell-${rowIndex}-${dayIndex}`}
                          data-testid={`multi-day-cell-${rowIndex}-${dayIndex}`}
                          className={cn(
                            'relative flex items-center justify-center border-r last:border-r-0',
                            todayIndex === dayIndex && 'bg-primary/10',
                            'flex-none',
                          )}
                          style={{ width: `${dayWidthPercent}%` }}
                        ></div>
                      ))}
                    </div>
                  ))}
                </div>
              </div>
              <MultiDayEventSection
                rows={multiDayEventRows}
                daysInWeek={weekDays}
                multiDayRowHeight={MULTI_DAY_ROW_HEIGHT}
                showEventDetail={showEventDetail}
                isExpanded={isMultiDayExpanded}
              />
            </div>
          </div>
        )}
      <div className="h-[835px]">
        <ScrollArea className="h-full w-full">
          <div className="flex flex-1 overflow-hidden">
            <TimeColumn
              ref={timeColumnRef}
              timeSlots={timeSlots}
              timeFormat={timeFormat}
              onTimeHover={handleTimeHover}
              onPreciseHover={handlePreciseHover}
              onLeave={handleTimeLeave}
              onTimeSlotClick={handleTimeSlotClick}
              variant="week"
            />
            <div ref={containerRef} className="relative flex-1">
              {viewSettings.days.showCurrentTimeIndicator && (
                <CurrentTimeIndicator
                  currentHour={currentHour}
                  currentMinute={currentMinute}
                  timeFormat={timeFormat}
                  hourHeight={HOUR_HEIGHT}
                  className="left-0"
                />
              )}
              {hoverPosition && viewSettings.days.showHoverTimeIndicator && (
                <HoverTimeIndicator
                  hour={hoverPosition.hour}
                  minute={hoverPosition.minute}
                  timeFormat={timeFormat}
                  hourHeight={HOUR_HEIGHT}
                  className="left-0"
                />
              )}
              <TimeGrid
                timeSlots={timeSlots}
                daysInWeek={weekDays}
                todayIndex={todayIndex}
                dayWidthPercent={dayWidthPercent}
                dynamicWidth={true}
                onTimeBlockClick={handleTimeBlockClick}
                highlightToday={viewSettings.days.highlightToday}
              />
              <div className="pointer-events-none absolute inset-0">
                {singleDayEvents.map((event) => {
                  const eventDate = new Date(event.startDate);
                  const dayIndex = weekDays.findIndex((day) =>
                    isSameDay(day, eventDate),
                  );
                  if (dayIndex === -1) return null;

                  const position = eventsPositions[`${dayIndex}-${event.id}`];
                  if (!position) return null;

                  const OVERLAP_FACTOR = 0.5;
                  const columnWidth =
                    (dayWidthPercent + OVERLAP_FACTOR / position.totalColumns) /
                    position.totalColumns;
                  const leftPercent =
                    dayIndex * dayWidthPercent +
                    position.column * columnWidth -
                    OVERLAP_FACTOR / (position.totalColumns * 2);
                  const rightPercent = 100 - (leftPercent + columnWidth);

                  return (
                    <EventDialogTrigger
                      event={event}
                      key={event.id}
                      position={position}
                      leftOffset={leftPercent}
                      rightOffset={rightPercent}
                      onClick={openEventDialog}
                    />
                  );
                })}
              </div>
            </div>
          </div>
        </ScrollArea>
      </div>
    </div>
  );
}

'use client';

import { useState } from 'react';
import { useQueryStates, parseAsArrayOf, parseAsString } from 'nuqs';
import { Search, X, Tag, Repeat, Clock } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Label } from '@/components/ui/label';
import { Badge } from '@/components/ui/badge';
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from '@/components/ui/select';
import { Checkbox } from '@/components/ui/checkbox';
import {
  Popover,
  PopoverContent,
  PopoverTrigger,
} from '@/components/ui/popover';
import { CATEGORY_OPTIONS, EVENT_COLORS } from '@/constants/calendar-constant';
import { getColorClasses } from '@/lib/event';
import { EventSearchDialog } from './event-search-dialog';
import { useShallow } from 'zustand/shallow';
import { useEventCalendarStore } from '@/hooks/use-event';

export const EventCalendarFilters = () => {
  const { timeFormat, openEventDialog } = useEventCalendarStore(
    useShallow((state) => ({
      timeFormat: state.timeFormat,
      openEventDialog: state.openEventDialog,
    })),
  );
  const [searchDialogOpen, setSearchDialogOpen] = useState(false);
  const [filters, setFilters] = useQueryStates({
    categories: parseAsArrayOf(parseAsString).withDefault([]),
    locations: parseAsArrayOf(parseAsString).withDefault([]),
    colors: parseAsArrayOf(parseAsString)
      .withDefault([])
      .withOptions({ shallow: false }),
    isRepeating: parseAsString.withDefault(''),
    repeatingTypes: parseAsArrayOf(parseAsString).withDefault([]),
    dateStart: parseAsString.withDefault(''),
    dateEnd: parseAsString.withDefault(''),
    search: parseAsString.withDefault(''),
  });

  const getActiveFiltersCount = () => {
    let count = 0;
    count += filters.categories.length;
    count += filters.locations.length;
    count += filters.colors.length;
    count += filters.repeatingTypes.length;
    if (filters.isRepeating) count += 1;
    if (filters.dateStart || filters.dateEnd) count += 1;
    if (filters.search) count += 1;
    return count;
  };

  const toggleArrayFilter = (key: keyof typeof filters, value: string) => {
    if (
      key === 'dateStart' ||
      key === 'dateEnd' ||
      key === 'search' ||
      key === 'isRepeating'
    )
      return;

    const currentArray = filters[key] as string[];
    const newArray = currentArray.includes(value)
      ? currentArray.filter((item) => item !== value)
      : [...currentArray, value];

    setFilters({ [key]: newArray });
  };

  const updateSingleFilter = (key: keyof typeof filters, value: string) => {
    setFilters({ [key]: value });
  };

  const clearAllFilters = () => {
    setFilters({
      categories: [],
      locations: [],
      colors: [],
      isRepeating: '',
      repeatingTypes: [],
      dateStart: '',
      dateEnd: '',
      search: '',
    });
  };

  const clearSingleArrayFilter = (key: keyof typeof filters, value: string) => {
    if (
      key === 'dateStart' ||
      key === 'dateEnd' ||
      key === 'search' ||
      key === 'isRepeating'
    )
      return;

    const currentArray = filters[key] as string[];
    const newArray = currentArray.filter((item) => item !== value);
    setFilters({ [key]: newArray });
  };

  const activeFiltersCount = getActiveFiltersCount();

  return (
    <div className="flex flex-col space-y-2 border-b px-4 pt-2 pb-2">
      <div className="flex flex-wrap items-center justify-center gap-3 sm:justify-start">
        <Button
          variant={filters.search ? 'default' : 'outline'}
          onClick={() => setSearchDialogOpen(true)}
          className="h-9 gap-2 px-4 text-sm font-medium transition-all"
        >
          <Search className="h-4 w-4" />
          Search Events
          {filters.search && (
            <Badge variant="secondary" className="ml-1">
              1
            </Badge>
          )}
        </Button>
        <Popover>
          <PopoverTrigger asChild>
