import { AnimatePresence, motion } from 'framer-motion';

type EventDialogTriggerProps = {
  event: Events;
  position: EventPosition;
  leftOffset: number;
  rightOffset: number;
  onClick: (
    event: Events,
    position: EventPosition,
    leftOffset: number,
    rightOffset: number,
  ) => void;
};

export const EventDialogTrigger = ({
  event,
  position,
  leftOffset,
  rightOffset,
  onClick,
}: EventDialogTriggerProps) => {
  const { bg } = getColorClasses(event.color);
  const handleClick = (e: React.MouseEvent<HTMLButtonElement>) => {
    e.preventDefault();
    e.stopPropagation();
    onClick(event, position, leftOffset, rightOffset);
  };

  return (
    <AnimatePresence>
      <motion.div
        initial={{ opacity: 0, y: 10, scale: 0.95 }}
        animate={{ opacity: 1, y: 0, scale: 1 }}
        exit={{ opacity: 0, y: -10, scale: 0.95 }}
        transition={{ duration: 0.2 }}
        style={{
          position: 'absolute',
          top: `${position?.top}px`,
          height: `${position?.height}px`,
          left: `calc(${leftOffset}% + 4px)`,
          right: `calc(${rightOffset}% + 4px)`,
          zIndex: 5,
        }}
      >
        <Button
          className={cn(
            'group absolute flex h-full w-full cursor-pointer flex-col items-start justify-start gap-0 overflow-hidden rounded bg-transparent p-2 text-white hover:bg-transparent',
            'border-none shadow-none ring-0 focus:ring-0 focus:outline-none',
            'transition-colors',
            bg,
          )}
          onClick={handleClick}
        >
          <div className="text-xs font-medium sm:truncate">{event.title}</div>
          <div className="text-xs sm:truncate">
            {formatTimeDisplay(event.startTime, '12')} -{' '}
            {formatTimeDisplay(event.endTime, '12')}
          </div>
          {position?.height && position.height > 40 && (
            <div className="mt-1 text-xs sm:truncate">
              {calculateDuration?.(event.startTime, event.endTime, 'auto')} Hour
            </div>
          )}
        </Button>
      </motion.div>
    </AnimatePresence>
  );
};

'use client';
import { useCallback } from 'react';
import { ScrollArea } from '@/components/ui/scroll-area';
import {
  addDays,
  endOfDay,
  isSameDay,
  isSameMonth,
  isSameWeek,
  isSameYear,
  isWithinInterval,
  startOfDay,
} from 'date-fns';
import { CalendarViewType, Events, TimeFormatType } from '@/types/event';
import { EventGroup, NoEvents } from './ui/events';
import { useEventCalendarStore } from '@/hooks/use-event';
import {
  getLocaleFromCode,
  useEventFilter,
  useEventGrouper,
} from '@/lib/event';
import { useShallow } from 'zustand/shallow';

interface EventsListProps {
  events: Events[];
  currentDate: Date;
}

export const EVENT_VIEW_CONFIG = {
  [CalendarViewType.DAY]: {
    groupFormat: 'HH:mm',
    titleFormat: 'EEEE, d MMMM yyyy',
    filterFn: (eventDate: Date, currentDate: Date) =>
      isSameDay(eventDate, currentDate),
  },
  [CalendarViewType.DAYS]: {
    groupFormat: 'EEEE, d MMMM',
    titleFormat: 'd MMMM yyyy',
    filterFn: (eventDate: Date, currentDate: Date, daysCount: number = 7) => {
      const start = startOfDay(currentDate);
      const end = endOfDay(addDays(currentDate, daysCount - 1));
      return isWithinInterval(eventDate, { start, end });
    },
  },
  [CalendarViewType.WEEK]: {
    groupFormat: 'yyyy-MM-dd',
    titleFormat: 'EEEE, d MMMM yyyy',
    filterFn: (eventDate: Date, currentDate: Date) =>
      isSameWeek(eventDate, currentDate),
  },
  [CalendarViewType.MONTH]: {
    groupFormat: 'yyyy-MM-dd',
    titleFormat: 'EEEE, d MMMM yyyy',
    filterFn: (eventDate: Date, currentDate: Date) =>
      isSameMonth(eventDate, currentDate),
  },
  [CalendarViewType.YEAR]: {
    groupFormat: 'yyyy-MM-dd',
    titleFormat: 'EEEE, d MMMM yyyy',
    filterFn: (eventDate: Date, currentDate: Date) =>
      isSameYear(eventDate, currentDate),
  },
};

const EventSection = ({
  title,
  timeKey,
  events,
  timeFormat,
  onEventClick,
}: {
  title: string;
  timeKey: string;
  events: Events[];
  timeFormat: TimeFormatType;
  onEventClick: (event: Events) => void;
}) => (
  <div className="space-y-2">
    <h3 className="text-muted-foreground text-sm font-medium">{title}</h3>
    <EventGroup
      timeKey={timeKey}
      events={events}
      timeFormat={timeFormat}
      onClick={onEventClick}
    />
  </div>
);

export function EventsList({ events, currentDate }: EventsListProps) {
  const { timeFormat, currentView, locale, openEventDialog } =
    useEventCalendarStore(
      useShallow((state) => ({
        timeFormat: state.timeFormat,
        currentView: state.currentView,
        locale: state.locale,
        openEventDialog: state.openEventDialog,
      })),
    );
  const localeObj = getLocaleFromCode(locale);

  const filteredEvents = useEventFilter(events, currentDate, currentView);

  const groupedEvents = useEventGrouper(
    filteredEvents,
    currentView,
    timeFormat,
    localeObj,
  );

  const handleEventClick = useCallback(
    (event: Events) => {
      openEventDialog(event);
    },
    [openEventDialog],
  );

  if (groupedEvents.length === 0) {
    return <NoEvents {...{ currentDate, currentView, locale: localeObj }} />;
  }

  return (
    <div className="h-full w-full space-y-4" data-testid="events-list">
      <ScrollArea className="h-[calc(100vh-12rem)] pr-3">
        <div className="space-y-3 px-5 py-4">
          {groupedEvents.map(({ key, title, events }) => (
            <EventSection
              key={key}
              title={title}
              timeKey={key}
              events={events}
              onEventClick={handleEventClick}
              timeFormat={timeFormat}
            />
          ))}
        </div>
      </ScrollArea>
    </div>
  );
}

