import { getColorClasses } from '@/lib/event';
import { cn } from '@/lib/utils';
import {
  eachDayOfInterval,
  endOfMonth,
  format,
  getDate,
  getMonth,
  isSameMonth,
  isSameYear,
  startOfMonth,
} from 'date-fns';
import { ChevronRight, Plus } from 'lucide-react';
import { memo, useMemo } from 'react';
import { Events, YearViewConfig } from '@/types/event';
import { parseAsIsoDate, useQueryState } from 'nuqs';

interface MonthCardProps {
  month: Date;
  eventsByDate: Record<string, Events[]>;
  eventCount: number;
  yearViewConfig: YearViewConfig;
  onMonthClick: (month: Date) => void;
  onEventClick: (event: Events) => void;
  onDateClick: (date: Date) => void;
  onQuickAdd: (date: Date) => void;
}

interface DayCellProps {
  day: Date;
  events: Events[];
  isToday: boolean;
  onClick: () => void;
}

const DayCell = memo(({ day, events, isToday, onClick }: DayCellProps) => {
  const hasDayEvents = events.length > 0;

  const tooltipContent = useMemo(
    () =>
      hasDayEvents
        ? `${events.length} Event on ${format(day, 'd MMMM yyyy')}`
        : format(day, 'd MMMM yyyy'),
    [hasDayEvents, events.length, day],
  );

  return (
    <Tooltip>
      <TooltipTrigger asChild>
        <button
          className={cn(
            'relative flex h-10 w-full items-center justify-center rounded-full p-0 text-[11px] transition-colors',
            isToday ? 'bg-blue-500 font-bold text-white' : '',
            hasDayEvents && !isToday ? 'bg-primary/10 font-medium' : '',
          )}
          onClick={onClick}
        >
          {getDate(day)}
          {hasDayEvents && !isToday && (
            <span className="bg-primary absolute -bottom-0.5 left-1/2 h-1 w-1 -translate-x-1/2 transform rounded-full" />
          )}
        </button>
      </TooltipTrigger>
      <TooltipContent side="top" align="center">
        {tooltipContent}
      </TooltipContent>
    </Tooltip>
  );
});

DayCell.displayName = 'DayCell';

const MonthDaysGrid = memo(
  ({
    month,
    eventsByDate,
    onDateClick,
  }: {
    month: Date;
    eventsByDate: Record<string, Events[]>;
    onDateClick: (date: Date) => void;
  }) => {
    const daysInMonth = eachDayOfInterval({
      start: startOfMonth(month),
      end: endOfMonth(month),
    });

    return (
      <div className="grid grid-cols-7 gap-1 text-center text-xs">
        {['S', 'M', 'T', 'W', 'T', 'F', 'S'].map((day, i) => (
          <div
            key={i}
            className="text-muted-foreground mb-1 text-[10px] font-medium"
          >
            {day}
          </div>
        ))}
        {daysInMonth.map((day) => {
          const dateKey = format(day, 'yyyy-MM-dd');
          return (
            <DayCell
              key={dateKey}
              day={day}
              events={eventsByDate[dateKey] || []}
              isToday={isSameDay(day, new Date())}
              onClick={() => onDateClick(day)}
            />
          );
        })}
      </div>
    );
  },
);

MonthDaysGrid.displayName = 'MonthDaysGrid';

const MonthCard = memo(
  ({
    month,
    eventsByDate,
    eventCount,
    yearViewConfig,
    onMonthClick,
    onEventClick,
    onDateClick,
    onQuickAdd,
  }: MonthCardProps) => {
    const _monthIndex = getMonth(month);
    const today = new Date();
    const isCurrentMonth =
      isSameMonth(month, today) && isSameYear(month, today);
    const hasEvents = eventCount > 0;
    const [, setDate] = useQueryState(
      'date',
      parseAsIsoDate.withOptions({
        shallow: false,
      }),
    );

    return (
      <div
        className={cn(
          'group flex flex-col rounded-lg border p-3 shadow-sm transition-all',
          'hover:border-primary hover:shadow-md',
          isCurrentMonth &&
            yearViewConfig.highlightCurrentMonth &&
            'border-blue-500 bg-blue-50/50 dark:bg-blue-950/10',
        )}
      >
        <div className="mb-3 flex items-center justify-between">
          <Tooltip>
            <TooltipTrigger asChild>
              <Button
                variant="ghost"
                className={cn(
                  'text-md flex h-auto items-center gap-1 p-0 font-medium hover:cursor-pointer',
                  isCurrentMonth && 'text-blue-600 dark:text-blue-400',
                  'transition-all hover:translate-x-0.5',
                )}
                onClick={() => {
                  const dateForQuery = new Date(month);
                  dateForQuery.setHours(12, 0, 0, 0);
                  setDate(dateForQuery);
                  onMonthClick(month);
                }}
              >
                {yearViewConfig.showMonthLabels && (
                  <span>{format(month, 'MMMM')}</span>
                )}
              </Button>
            </TooltipTrigger>
            <TooltipContent side="top">
              View {format(month, 'MMMM yyyy')}
            </TooltipContent>
          </Tooltip>
          <Tooltip>
            <TooltipTrigger asChild>
              <Button
                variant="outline"
                size="icon"
                className="h-7 w-7 rounded-full opacity-0 transition-opacity group-hover:opacity-100"
                onClick={() => onQuickAdd(month)}
              >
                <Plus className="h-3.5 w-3.5" />
              </Button>
            </TooltipTrigger>
            <TooltipContent side="top">
              Add event in {format(month, 'MMMM')}
            </TooltipContent>
          </Tooltip>
        </div>

        <MonthDaysGrid
          month={month}
          eventsByDate={eventsByDate}
          onDateClick={onDateClick}
        />

        {hasEvents && yearViewConfig.enableEventPreview ? (
          <div className="space-y-1 pt-1">
            {Object.entries(eventsByDate)
              .filter(([key]) => key.startsWith(format(month, 'yyyy-MM')))
              .slice(0, yearViewConfig.previewEventsPerMonth)
              .flatMap(([dateKey, events]) =>
                events.slice(0, 1).map((event) => {
                  const colorClasses = getColorClasses(event.color);
                  return (
                    <button
                      key={event.id}
                      className={cn(
                        'w-full truncate rounded-md px-2 py-1.5 text-left text-xs',
                        colorClasses?.bg,
                      )}
                      onClick={() => onEventClick(event)}
                    >
                      <span className="flex items-center text-white">
                        <span className="mr-1 font-medium">
                          {format(new Date(dateKey), 'd')}
                        </span>
                        {event.title}
                      </span>
                    </button>
                  );
                }),
              )}
            {eventCount > 3 && yearViewConfig.showMoreEventsIndicator && (
              <Button
                variant="ghost"
                size="sm"
                className="hover:bg-accent/40 mt-1 h-7 w-full justify-between px-2 py-1 text-xs"
                onClick={() => onMonthClick(month)}
              >
                <span>View all {eventCount} events</span>
                <ChevronRight className="h-3 w-3" />
              </Button>
            )}
          </div>
        ) : null}
      </div>
    );
  },
);

MonthCard.displayName = 'MonthCard';

export { MonthCard };

import { Button } from '@/components/ui/button';
import { cn } from '@/lib/utils';
import { memo } from 'react';
import { motion, AnimatePresence } from 'motion/react';
import { EventTypes } from '@/db/schema';
import { getColorClasses } from '@/lib/event';
import { formatTimeDisplay, calculateDuration } from '@/lib/date';

interface MultiDayEventProps {
  event: EventTypes;
  startIndex: number;
  endIndex: number;
  row: number;
  onClick: (event: EventTypes) => void;
  daysCount: number;
  multiDayRowHeight: number;
}

interface MultiDayEventRowType {
  event: EventTypes;
  startIndex: number;
  endIndex: number;
  row: number;
}

interface MultiDayEventSectionProps {
  rows: MultiDayEventRowType[];
  daysInWeek: Date[];
  showEventDetail: (event: EventTypes) => void;
  multiDayRowHeight: number;
  isExpanded?: boolean;
}

export const MultiDayEvent = ({
  event,
  startIndex,
  endIndex,
  row,
  onClick,
  daysCount,
  multiDayRowHeight,
}: MultiDayEventProps) => {
  const dayWidth = 100 / daysCount;
  const eventLeftPercent = startIndex * dayWidth;
  const eventWidthPercent = (endIndex - startIndex + 1) * dayWidth;
  const { bg } = getColorClasses(event.color);

  return (
    <motion.div
      initial={{ opacity: 0, y: 10, scale: 0.95 }}
      animate={{ opacity: 1, y: 0, scale: 1 }}
      exit={{ opacity: 0, y: -10, scale: 0.95 }}
      transition={{ duration: 0.2 }}
      className="absolute"
      style={{
        left: `calc(${eventLeftPercent}% + 1px)`,
        width: `calc(${eventWidthPercent}% - 2px)`,
        top: `${row * multiDayRowHeight}px`,
        height: `${multiDayRowHeight - 1}px`,
      }}
    >
      <Button
        className={cn(
          'group absolute flex h-full w-full cursor-pointer flex-col items-start justify-start gap-0 overflow-hidden rounded bg-transparent p-2 text-white hover:bg-transparent',
          'border-none shadow-none ring-0 focus:ring-0 focus:outline-none',
          'transition-colors',
          bg,
        )}
        onClick={() => onClick(event)}
      >
        <div className="text-xs font-medium sm:truncate">{event.title}</div>
        <div className="text-xs sm:truncate">
          {formatTimeDisplay(event.startTime, '12')} -{' '}
          {formatTimeDisplay(event.endTime, '12')}
        </div>
        <div className="mt-1 text-xs sm:truncate">
          {calculateDuration(event.startTime, event.endTime, 'auto')}
        </div>
      </Button>
    </motion.div>
  );
};

MultiDayEvent.displayName = 'MultiDayEvent';

export const MultiDayEventSection = memo(
  ({
    rows,
    daysInWeek,
    showEventDetail,
    multiDayRowHeight,
    isExpanded = false,
  }: MultiDayEventSectionProps) => {
    const visibleEvents = isExpanded
      ? rows
      : rows.filter(({ row }) => row === 0);

    return (
      <div className="absolute inset-0 overflow-hidden">
        <AnimatePresence initial={false}>
          {visibleEvents.map(({ event, startIndex, endIndex, row }) => (
            <MultiDayEvent
              key={event.id}
              event={event}
              startIndex={startIndex}
              endIndex={endIndex}
              row={row}
              onClick={showEventDetail}
              daysCount={daysInWeek.length}
              multiDayRowHeight={multiDayRowHeight}
            />
          ))}
        </AnimatePresence>
      </div>
    );
  },
);

MultiDayEventSection.displayName = 'MultiDayEventSection';

'use client';

import { useState, useMemo, useEffect, useTransition } from 'react';
import {
  format,
  getDate,
  getDaysInMonth,
  getMonth,
  getYear,
  Locale,
} from 'date-fns';
import { enUS } from 'date-fns/locale';
import { Button } from '@/components/ui/button';
import {
  Popover,
  PopoverContent,
  PopoverTrigger,
} from '@/components/ui/popover';
import {
  Command,
  CommandEmpty,
  CommandGroup,
  CommandInput,
  CommandItem,
  CommandList,
} from '@/components/ui/command';
import { Check, ChevronsUpDown } from 'lucide-react';
import { cn } from '@/lib/utils';
import { ScrollArea, ScrollBar } from '../../ui/scroll-area';
import { motion, AnimatePresence } from 'framer-motion';
import { useQueryState } from 'nuqs';
import { parseAsIsoDate } from 'nuqs/server';
import { Skeleton } from '@/components/ui/skeleton';

interface SearchDayPickerProps {
  locale?: Locale;
  className?: string;
  placeholder?: string;
  weekStartsOn?: 0 | 1 | 2 | 3 | 4 | 5 | 6;
}

export function SearchDayPicker({
  locale = enUS,
  className = '',
  placeholder = 'Choose Day',
  weekStartsOn = 1, // Monday as default first day of week
}: SearchDayPickerProps) {
  const [open, setOpen] = useState(false);
  const [searchValue, setSearchValue] = useState('');
  const [inputValue, setInputValue] = useState('');
  const [selectedDayChanged, setSelectedDayChanged] = useState(false);
  const [isPending, startTransition] = useTransition();
  const [date, setDate] = useQueryState(
    'date',
    parseAsIsoDate.withDefault(new Date()).withOptions({
      shallow: false,
      startTransition,
    }),
  );

  const day = getDate(date);

  /**
   * Gets the day suffix (st, nd, rd, th) for a given day number
   */
  const getDaySuffix = useMemo(() => {
    return (day: number): string => {
      if (day >= 11 && day <= 13) return 'th';
      switch (day % 10) {
        case 1:
          return 'st';
        case 2:
          return 'nd';
        case 3:
          return 'rd';
        default:
          return 'th';
      }
    };
  }, []);

  const daysInMonth = useMemo(() => {
    const year = getYear(date);
    const month = getMonth(date);
    const daysCount = getDaysInMonth(new Date(year, month));

    return Array.from({ length: daysCount }, (_, i) => {
      const dayNum = i + 1;
      const dayDate = new Date(year, month, dayNum);
      const dayName = format(dayDate, 'EEE', { locale, weekStartsOn });
      const fullDayName = format(dayDate, 'EEEE', { locale, weekStartsOn });
      const daySuffix = getDaySuffix(dayNum);

      return {
        value: dayNum.toString(),
        day: dayNum,
        dayName,
        fullDayName,
        daySuffix,
        formattedDate: format(dayDate, 'd MMMM', { locale }),
        label: `${dayName} ${dayNum}${daySuffix}`,
        searchableText:
          `${fullDayName} ${dayNum} ${format(dayDate, 'd MMMM', { locale })}`.toLowerCase(),
      };
    });
  }, [date, locale, weekStartsOn, getDaySuffix]);

  const filteredDays = useMemo(() => {
    if (!searchValue) return daysInMonth;
    const searchTerm = searchValue.toLowerCase();
    return daysInMonth.filter(
      (day) =>
        day.searchableText.includes(searchTerm) ||
        day.day.toString().includes(searchTerm),
    );
  }, [daysInMonth, searchValue]);

  const selectedDay = daysInMonth.find((d) => d.day === day) || daysInMonth[0];

  const handleDayChange = (dayValue: string) => {
    const newDay = parseInt(dayValue);
    const newDate = new Date(date);
    newDate.setDate(newDay);

    setDate(newDate);
    setOpen(false);
    setSearchValue('');
    setSelectedDayChanged(true);

    setTimeout(() => {
      setSelectedDayChanged(false);
    }, 1000);
  };

  useEffect(() => {
    if (!open) {
      setSearchValue('');
      setInputValue('');
    }
  }, [open]);

  if (isPending) {
    <Skeleton />;
  }

  return (
    <Popover open={open} onOpenChange={setOpen}>
      <PopoverTrigger asChild>
        <motion.div
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ duration: 0.3 }}
          whileHover={{ scale: 1.02 }}
        >
          <Button
            variant="outline"
            role="combobox"
            aria-expanded={open}
            className={cn(
              'w-[130px] justify-between text-sm font-normal',
              !selectedDay && 'text-muted-foreground',
              className,
            )}
            title="Choose a day"
          >
            {selectedDay ? (
              <AnimatePresence mode="wait">
                <motion.div
                  key={selectedDay.label}
                  initial={{
                    y: selectedDayChanged ? 11 : 0,
                    opacity: selectedDayChanged ? 0 : 1,
                  }}
                  animate={{ y: 0, opacity: 1 }}
                  exit={{ y: -10, opacity: 0 }}
                  transition={{ duration: 0.2 }}
                >
                  <div className="flex items-center gap-2">
                    <span className="text-muted-foreground italic">
                      {selectedDay.dayName}
                    </span>
                    <span>
                      {selectedDay.day}
                      <sup>{selectedDay.daySuffix}</sup>
                    </span>
                  </div>
                </motion.div>
              </AnimatePresence>
            ) : (
              placeholder
            )}
            <ChevronsUpDown className="ml-2 h-4 w-4 shrink-0 opacity-50" />
          </Button>
        </motion.div>
      </PopoverTrigger>
      <PopoverContent className="w-[130px] p-0" align="start">
        <Command shouldFilter={false}>
          <CommandInput
            placeholder="Cari hari..."
            value={inputValue}
            onValueChange={(value) => {
              setInputValue(value);
              setSearchValue(value);
            }}
          />
          <CommandList>
            <CommandEmpty>Hari tidak ditemukan</CommandEmpty>
            <CommandGroup>
              <ScrollArea className="h-[200px]">
                {filteredDays.map((day) => (
                  <CommandItem
                    key={day.value}
                    value={day.value}
                    onSelect={handleDayChange}
                    className="flex items-center justify-between"
                  >
                    <div className="flex items-center gap-2">
                      <span className="text-muted-foreground italic">
                        {day.dayName}
                      </span>
                      <span>
                        {day.day}
                        <sup>{day.daySuffix}</sup>
                      </span>
                    </div>
                    <Check
                      className={cn(
                        'h-4 w-4',
                        selectedDay.day === day.day
                          ? 'opacity-100'
                          : 'opacity-0',
                      )}
                    />
                  </CommandItem>
                ))}
                <ScrollBar orientation="vertical" />
              </ScrollArea>
            </CommandGroup>
          </CommandList>
        </Command>
      </PopoverContent>
    </Popover>
  );
}

'use client';

import { useState, useMemo, useEffect } from 'react';
import { format, getMonth, setMonth, Locale } from 'date-fns';
import { Button } from '@/components/ui/button';
import {
  Popover,
  PopoverContent,
  PopoverTrigger,
} from '@/components/ui/popover';
import {
  Command,
  CommandEmpty,
  CommandGroup,
  CommandInput,
  CommandItem,
  CommandList,
} from '@/components/ui/command';
import { CalendarIcon, Check, ChevronsUpDown } from 'lucide-react';
import { cn } from '@/lib/utils';
import { ScrollArea, ScrollBar } from '../../ui/scroll-area';
import { motion, AnimatePresence } from 'framer-motion';
import { useQueryState } from 'nuqs';
import { parseAsIsoDate } from 'nuqs/server';
import { enUS } from 'date-fns/locale';
interface SearchableMonthPickerProps {
  locale: Locale;
  className?: string;
  monthFormat?: string;
  placeholder?: string;
}

export function SearchMonthPicker({
  locale = enUS,
  className = '',
  monthFormat = 'MMMM',
  placeholder = 'Select month',
}: SearchableMonthPickerProps) {
  const [open, setOpen] = useState(false);
  const [searchValue, setSearchValue] = useState('');
  const [inputValue, setInputValue] = useState('');
  const [selectedMonthChange, setSelectedMonthChanged] =
    useState<boolean>(false);
  const [date, setDate] = useQueryState(
    'date',
    parseAsIsoDate.withDefault(new Date()).withOptions({
      shallow: false,
    }),
  );

  const month = getMonth(date);

  const months = useMemo(() => {
    return Array.from({ length: 12 }, (_, i) => ({
      value: i.toString(),
      label: format(new Date(2000, i, 1), monthFormat, { locale }),
      shortLabel: format(new Date(2000, i, 1), 'MMM', { locale }),
    }));
  }, [locale, monthFormat]);

  const filteredMonths = useMemo(() => {
    if (!searchValue) return months;
    return months.filter(
      (m) =>
        m.label.toLowerCase().includes(searchValue.toLowerCase()) ||
        m.shortLabel.toLowerCase().includes(searchValue.toLowerCase()),
    );
  }, [months, searchValue]);

  const selectedMonth = months[month];

  const handleMonthChange = (monthValue: string) => {
    const newMonth = parseInt(monthValue);
    const newDate = setMonth(date, newMonth);

    setDate(newDate);
    setOpen(false);
    setSearchValue('');
    setSelectedMonthChanged(true);

    setTimeout(() => setSelectedMonthChanged(false), 1000);
  };

  useEffect(() => {
    if (!open) {
      setSearchValue('');
      setInputValue('');
    }
  }, [open]);

  return (
    <Popover open={open} onOpenChange={setOpen}>
      <PopoverTrigger asChild>
        <motion.div
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ duration: 0.3 }}
          whileHover={{ scale: 1.02 }}
        >
          <Button
            variant="outline"
            role="combobox"
            aria-expanded={open}
            className={cn(
              'w-[150px] justify-between text-sm font-normal',
              !selectedMonth && 'text-muted-foreground',
              className,
            )}
            title="Select month"
          >
            <div className="flex items-center">
              <CalendarIcon className="mr-2 h-4 w-4" />
              <AnimatePresence mode="wait">
                <motion.div
                  key={selectedMonth?.label}
                  initial={{
                    y: selectedMonthChange ? 11 : 0,
                    opacity: selectedMonthChange ? 0 : 1,
                  }}
                  animate={{ y: 0, opacity: 1 }}
                  exit={{ y: -10, opacity: 0 }}
                  transition={{ duration: 0.2 }}
                >
                  <span className="truncate">
                    {selectedMonth?.label || placeholder}
                  </span>
                </motion.div>
              </AnimatePresence>
            </div>
            <ChevronsUpDown className="ml-2 h-4 w-4 shrink-0 opacity-50" />
          </Button>
        </motion.div>
      </PopoverTrigger>
      <PopoverContent className="w-[200px] p-0" align="start">
        <Command shouldFilter={false}>
          <CommandInput
            placeholder="Search month..."
            value={inputValue}
            onValueChange={(value) => {
              setInputValue(value);
              setSearchValue(value);
            }}
          />
          <CommandList>
            <CommandEmpty>Month not found</CommandEmpty>
            <CommandGroup>
              <ScrollArea className="h-[200px]">
                {filteredMonths.map((m) => (
                  <CommandItem
                    key={m.value}
                    value={m.value}
                    onSelect={handleMonthChange}
                  >
                    <Check
                      className={cn(
                        'mr-2 h-4 w-4',
                        month === parseInt(m.value)
                          ? 'opacity-100'
                          : 'opacity-0',
                      )}
                    />
                    <span className="flex-1">{m.label}</span>
                    <span className="text-muted-foreground mr-2 text-sm">
                      {m.shortLabel}
                    </span>
                  </CommandItem>
                ))}
                <ScrollBar orientation="vertical" />
              </ScrollArea>
            </CommandGroup>
          </CommandList>
        </Command>
      </PopoverContent>
    </Popover>
  );
}

'use client';

import { useState, useMemo, useEffect } from 'react';
import { getYear, setYear } from 'date-fns';
import { Button } from '@/components/ui/button';
import {
  Popover,
  PopoverContent,
  PopoverTrigger,
} from '@/components/ui/popover';
import {
  Command,
  CommandEmpty,
  CommandGroup,
  CommandInput,
  CommandItem,
  CommandList,
} from '@/components/ui/command';
import { Check, ChevronsUpDown } from 'lucide-react';
import { cn } from '@/lib/utils';
import { ScrollArea, ScrollBar } from '../../ui/scroll-area';
import { motion, AnimatePresence } from 'motion/react';
import { useQueryState } from 'nuqs';
import { parseAsIsoDate } from 'nuqs/server';

interface SearchYearPickerProps {
  yearRange?: number;
  className?: string;
  minYear?: number;
  maxYear?: number;
}

export function SearchYearPicker({
  yearRange = 10,
  className = '',
  minYear,
  maxYear,
}: SearchYearPickerProps) {
  const [open, setOpen] = useState(false);
  const [searchValue, setSearchValue] = useState('');
  const [inputValue, setInputValue] = useState('');
  const [selectedYearChanged, setSelectedYearChanged] = useState(false);
  const [date, setDate] = useQueryState(
    'date',
    parseAsIsoDate.withDefault(new Date()).withOptions({
      shallow: false,
    }),
  );

  const year = getYear(date);

  const years = useMemo(() => {
    const startYear = minYear ?? year - yearRange;
    const endYear = maxYear ?? year + yearRange;
    const yearsArray = [];

    for (let y = startYear; y <= endYear; y++) {
      yearsArray.push({
        value: y.toString(),
        label: y.toString(),
      });
    }

    return yearsArray;
  }, [year, yearRange, minYear, maxYear]);

  const filteredYears = useMemo(() => {
    if (!searchValue) return years;
    return years.filter((y) => y.label.includes(searchValue));
  }, [years, searchValue]);

  const handleYearChange = (yearValue: string) => {
    const newYear = parseInt(yearValue);
    const newDate = setYear(date, newYear);

    setDate(newDate);
    setOpen(false);
    setSearchValue('');
    setSelectedYearChanged(true);

    // Reset animation after a delay
    setTimeout(() => setSelectedYearChanged(false), 1000);
  };

  useEffect(() => {
    if (!open) {
      setSearchValue('');
      setInputValue('');
    }
  }, [open]);

  return (
    <Popover open={open} onOpenChange={setOpen}>
      <PopoverTrigger asChild>
        <motion.div
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ duration: 0.3 }}
          whileHover={{ scale: 1.02 }}
        >
          <Button
            variant="outline"
            role="combobox"
            aria-expanded={open}
            className={cn(
              'w-[120px] justify-between text-sm font-normal',
              !year && 'text-muted-foreground',
              className,
            )}
            title="Select year"
          >
            <AnimatePresence mode="wait">
              <motion.div
                key={year}
                initial={{
                  y: selectedYearChanged ? 10 : 0,
                  opacity: selectedYearChanged ? 0 : 1,
                }}
                animate={{ y: 0, opacity: 1 }}
                exit={{ y: -10, opacity: 0 }}
                transition={{ duration: 0.2 }}
              >
                {year || 'Pilih tahun'}
              </motion.div>
            </AnimatePresence>
            <ChevronsUpDown className="ml-2 h-4 w-4 shrink-0 opacity-50" />
          </Button>
        </motion.div>
      </PopoverTrigger>
      <PopoverContent className="w-[120px] p-0" align="start">
        <Command shouldFilter={false}>
          <CommandInput
            placeholder="Search year..."
            value={inputValue}
            onValueChange={(value) => {
              setInputValue(value);
              setSearchValue(value);
            }}
          />
          <CommandList>
            <CommandEmpty>Year not found</CommandEmpty>
            <CommandGroup>
              <ScrollArea className="h-[200px]">
                {filteredYears.map((y) => (
                  <CommandItem
                    key={y.value}
                    value={y.value}
